// no direct access
defined('_JEXEC') or die;



// Init some API objects
// ================================================================================
$app 	= JFactory::getApplication() ;
$date 	= JFactory::getDate( 'now' , JFactory::getConfig()->get('offset') ) ;
$doc 	= JFactory::getDocument();
$uri 	= JFactory::getURI() ;
$user	= JFactory::getUser();
$userId	= $user->get('id');



// List Control
// ================================================================================
$listOrder	= $this->state->get('list.ordering');
$listDirn	= $this->state->get('list.direction');
$canOrder	= $user->authorise('core.edit.state', 'com_quickcontent');
$saveOrder	= $listOrder == 'a.ordering' || ($listOrder == 'a.lft' && $listDirn == 'asc');
$trashed	= $this->state->get('filter.published') == -2 ? true : false;
$nested		= $this->state->get('items.nested') ;
$orderCol 	= $nested ? 'a.lft' : 'a.ordering' ;
$show_root	= JRequest::getVar('show_root') ;


// For Joomla!3.0
// ================================================================================
if( JVERSION >= 3 ) {
	if ($saveOrder)
	{
		$method = $nested ? 'saveOrderNestedAjax' : 'saveOrderAjax' ;
		$saveOrderingUrl = 'index.php?option=com_quickcontent&task=lists.'.$method.'&tmpl=component';
		JHtml::_('sortablelist.sortable', 'itemList', 'adminForm', strtolower($listDirn), $saveOrderingUrl, false, $nested);
	}
}
?>

<!-- List Table -->
<table class="table table-striped adminlist" id="itemList">
	<thead>
		<tr>
			<?php if( JVERSION >= 3 ): ?>
			<th width="1%" class="nowrap center hidden-phone">
				<?php echo JHtml::_('grid.sort', '<i class="icon-menu-2"></i>', $orderCol, $listDirn, $listOrder, null, 'asc', 'JGRID_HEADING_ORDERING'); ?>
			</th>
			<?php endif; ?>
			
			<th width="1%">
				<input type="checkbox" name="checkall-toggle" value="" onclick="Joomla.checkAll(this)" />
			</th>
			
			<th width="15%">
				<?php echo JHtml::_('grid.sort',  'JGLOBAL_TITLE', 'a.title', $listDirn, $listOrder); ?>
			</th>
			
			<th width="7%" class='left' class="nowrap">
			<?php echo JHtml::_('grid.sort',  '選單類型', 'a.menutype', $listDirn, $listOrder); ?>
			</th>
			<th width="7%" class='left' class="nowrap">
			<?php echo JHtml::_('grid.sort',  '顯示形式', 'a.category_menutype', $listDirn, $listOrder); ?>
			</th>
			<th width="7%" class='left' class="nowrap">
			<?php echo JHtml::_('grid.sort',  '清空選單', 'a.delete_existing', $listDirn, $listOrder); ?>
			</th>
			<th class='left'>
			<?php echo JHtml::_('grid.sort',  '內容', 'a.content', $listDirn, $listOrder); ?>
			</th>
			
			<th>
			產生選單
			</th>
			<th>
			移除上次產生的項目
			</th>
			
			<th width="5%" class="nowrap">
				<?php echo JHtml::_('grid.sort',  'JPUBLISHED', 'a.published', $listDirn, $listOrder); ?>
			</th>
			
			<?php if( JVERSION < 3 ): ?>
			<th width="10%">
				<?php echo JHtml::_('grid.sort',  'JGRID_HEADING_ORDERING', $orderCol, $listDirn, $listOrder); ?>
				<?php if ($canOrder && $saveOrder) :?>
					<?php echo JHtml::_('grid.order',  $this->items, 'filesave.png', 'lists.saveorder'); ?>
				<?php endif; ?>
			</th>
			<?php endif; ?>
			
			
			<th width="1%" class="nowrap">
				<?php echo JHtml::_('grid.sort',  'JGRID_HEADING_ID', 'a.id', $listDirn, $listOrder); ?>
			</th>

		</tr>
	</thead>
	<tfoot>
		<tr>
			<td colspan="15">
				<div class="pull-left">
					<?php echo $this->pagination->getListFooter(); ?>
				</div>
				
				<?php if( JVERSION >= 3 ): ?>
				<!-- Limit Box -->
				<div class="btn-group pull-right hidden-phone">
					<label for="limit" class="element-invisible"><?php echo JText::_('JFIELD_PLG_SEARCH_SEARCHLIMIT_DESC');?></label>
					<?php echo $this->pagination->getLimitBox(); ?>
				</div>
				<?php endif; ?>
			</td>
		</tr>
	</tfoot>
	<tbody>
	<?php foreach ($this->items as $i => $item) :
		
		$item = new JObject($item);
		
		$ordering	= ($listOrder == $orderCol);
		$canEdit	= $user->authorise('core.edit',			'com_quickcontent.list.'.$item->a_id);
		$canCheckin	= $user->authorise('core.manage',		'com_quickcontent.list.'.$item->a_id) || $item->a_checked_out == $userId || $item->a_checked_out == 0;
		$canChange	= $user->authorise('core.edit.state',	'com_quickcontent.list.'.$item->a_id) && $canCheckin;
		$canEditOwn = $user->authorise('core.edit.own',		'com_quickcontent.list.'.$item->a_id) && $item->c_id == $userId;
		
		// Nested ordering
		if($nested){
			
			if($item->a_id == 1) {
				$item->a_title = JText::_('JGLOBAL_ROOT') ;
				$canEdit 	= false ;
				$canChange 	= false ;
				$canEditOwn = false ;
			}
			
			$orderkey = array_search($item->a_id, $this->ordering[$item->a_parent_id]);
		
			// Get the parents of item for sorting
			if ($item->a_level > 1)
			{
				$parentsStr = "";
				$_currentParentId = $item->a_parent_id;
				$parentsStr = " ".$_currentParentId;
				for ($n = 0; $n < $item->a_level; $n++)
				{
					foreach ($this->ordering as $k => $v)
					{
						$v = implode("-", $v);
						$v = "-".$v."-";
						if (strpos($v, "-" . $_currentParentId . "-") !== false)
						{
							$parentsStr .= " ".$k;
							$_currentParentId = $k;
							break;
						}
					}
				}
			}
			else
			{
				$parentsStr = "";
			}
		}

		?>
		<tr class="row<?php echo $i % 2; ?>"
			<?php if( $nested ): ?>
				sortable-group-id="<?php echo $item->a_parent_id ;?>" item-id="<?php echo $item->a_id?>" parents="<?php echo $parentsStr?>" level="<?php echo $item->a_level?>"
			<?php else: ?>
				sortable-group-id="<?php echo $item->a_catid ;?>"
			<?php endif; ?>
		>
		<?php if( JVERSION >= 3 ): ?>
			<!-- Drag sort for Joomla!3.0 -->
			<td class="order nowrap center hidden-phone">
			<?php if ($canChange) :
				$disableClassName = '';
				$disabledLabel	  = '';

				if (!$saveOrder) :
					$disabledLabel    = JText::_('JORDERINGDISABLED');
					$disableClassName = 'inactive tip-top';
				endif; ?>
				<span class="sortable-handler hasTooltip <?php echo $disableClassName?>" title="<?php echo $disabledLabel?>">
					<i class="icon-menu"></i>
				</span>
				<input type="text" style="display:none" name="order[]" size="5" value="<?php echo $nested ? $orderkey + 1 : $item->a_ordering;?>" class="width-20 text-area-order " />
			<?php else : ?>
				<span class="sortable-handler inactive" >
					<i class="icon-menu"></i>
				</span>
			<?php endif; ?>
			</td>
		<?php endif; ?>
		
			<td class="center">
				<?php echo JHtml::_('grid.id', $i, $item->a_id); ?>
			</td>
			
			<td class="n/owrap has-context">
				<!-- Nested dashs -->
				<?php if( $nested ): ?>
				<div class="pull-left fltlft">
					<?php $offset = $show_root ? 0 : 1 ; ?>
					<?php echo str_repeat('<span class="gi">&mdash;</span>', $item->a_level - $offset) ; ?>
				</div>
				<?php endif; ?>
				
				<div class="pull-left fltlft">
				
				
				<!-- Checkout -->
				<?php if ($item->get('a_checked_out')) : ?>
					<?php echo JHtml::_('jgrid.checkedout', $i, $item->get('a_checked_out'), $item->get('a_checked_out_time'), 'lists.', $canCheckin); ?>
				<?php endif; ?>
				
				
				<!-- Title -->
				<?php if ($canEdit || $canEditOwn) : ?>
					<a href="<?php echo JRoute::_('index.php?option=com_quickcontent&task=list.edit&id='.$item->a_id); ?>">
						<?php echo $item->get('a_title'); ?>
					</a>
				<?php else: ?>
					<?php echo $item->get('a_title'); ?>
				<?php endif; ?>
				
				
				
				<?php if( JVERSION >= 3 ): ?>
				<!-- Title Edit Button -->
				<div class="pull-left">
					<?php
						// Create dropdown items
						if($canEdit || $canEditOwn){
							JHtml::_('dropdown.edit', $item->a_id, 'list.');
							JHtml::_('dropdown.divider');
						}
						
						
						if($canChange || $canEditOwn) {
							if ($item->a_published) :
							JHtml::_('dropdown.unpublish', 'cb' . $i, 'lists.');
							else :
								JHtml::_('dropdown.publish', 'cb' . $i, 'lists.');
							endif;
							JHtml::_('dropdown.divider');
						}
						
						
						if ($item->a_checked_out && $canCheckin) :
							JHtml::_('dropdown.checkin', 'cb' . $i, 'lists.');
						endif;
						
						if($canChange || $canEditOwn) {
							if ($trashed) :
								JHtml::_('dropdown.untrash', 'cb' . $i, 'lists.');
							else :
								JHtml::_('dropdown.trash', 'cb' . $i, 'lists.');
							endif;
						}
						
						// Render dropdown list
						echo JHtml::_('dropdown.render');
						?>
				</div>
				<?php endif; ?>
			</td>
			
			<td>
					<?php echo $item->a_menutype ; ?>
				</td>
				<td>
					<?php 
					switch($item->category_menutype ){
						case 'list' : echo '清單' ;		break;
						case 'blog' : echo '部落格' ;		break;
						default 	: echo '無' ;		break;
					}
					?>
				</td>
				<td>
					<?php 
						if($item->delete_existing)
							echo '是' ;
						else
							echo '否'; 
					?>
				</td>
				<td>
					<?php echo JString::substr( strip_tags($item->content) , 0 , 150 ).'...'; ?>
				</td>
				

				<td class="center">
					<span >
						<a style="<?php echo JVERSION < 3 ? 'width:32px;height:32px;text-indent:-5000px;display:inline-block;background:url(templates/bluestork/images/toolbar/icon-32-new.png);' : '' ; ?>" 
						href="index.php?option=com_quickcontent&task=generator.generate&id=<?php echo $item->a_id ?>"
						class="btn btn-primary">
						<i class="icon-new icon-white"></i></a>
					</span>
				</td>
				
				<td class="center">
					<span >
						<a style="<?php echo JVERSION < 3 ? 'width:32px;height:32px;text-indent:-5000px;display:inline-block;background:url(templates/bluestork/images/toolbar/icon-32-refresh.png);' : '' ; ?>" 
						href="index.php?option=com_quickcontent&task=generator.restore&id=<?php echo $item->a_id ?>"
						class="btn btn-danger">
						<i class="icon-refresh iconwhite"></i></a>
					</span>
				</td>
			
			<td class="center">
				<?php echo JHtml::_('jgrid.published', $item->a_published, $i, 'lists.', $canChange, 'cb', $item->a_publish_up, $item->a_publish_down); ?>
			</td>
			
			<?php if( JVERSION < 3 ): ?>
			<td class="order">
				<?php if ($canChange) : ?>
					<?php if ($saveOrder) :?>
						<?php if ($listDirn == 'asc') : ?>
							<span><?php echo $this->pagination->orderUpIcon($i, true, 'lists.orderup', 'JLIB_HTML_MOVE_UP', $ordering); ?></span>
							<span><?php echo $this->pagination->orderDownIcon($i, $this->pagination->total, true, 'lists.orderdown', 'JLIB_HTML_MOVE_DOWN', $ordering); ?></span>
						<?php elseif ($listDirn == 'desc') : ?>
							<span><?php echo $this->pagination->orderUpIcon($i, true, 'lists.orderdown', 'JLIB_HTML_MOVE_UP', $ordering); ?></span>
							<span><?php echo $this->pagination->orderDownIcon($i, $this->pagination->total, true, 'lists.orderup', 'JLIB_HTML_MOVE_DOWN', $ordering); ?></span>
						<?php endif; ?>
					<?php endif; ?>
					<?php $disabled = $saveOrder ?  '' : 'disabled="disabled"'; ?>
					<input type="text" name="order[]" size="5" value="<?php echo $nested ? $orderkey + 1 : $item->get('a_ordering');?>" <?php echo $disabled ?> class="text-area-order input-mini" />
				<?php else : ?>
					<?php echo $nested ? $orderkey + 1 : $item->get('a_ordering');?>
				<?php endif; ?>
			</td>
			<?php endif; ?>
			

			<td class="center">
				<span
					<?php if( $nested ): ?>
					class="hasTip" title="<?php echo $item->a_lft.'-'.$item->a_rgt; ?>"
					<?php endif; ?>
				><?php echo (int) $item->get('a_id'); ?></span>
			</td>

		</tr>
		<?php endforeach; ?>
	</tbody>
</table>